void TrvFilesE(const char * dir)
{
	char dirNew[200];
	strcpy(dirNew, dir);
	strcat(dirNew, "\\*.*");    // 在目录后面加上"\\*.*"进行第一次搜索

	intptr_t handle;
	_finddata_t findData;

	handle = _findfirst(dirNew, &findData);
	if (handle == -1)        // 检查是否成功
		return;
	do
	{
		if (findData.attrib & _A_SUBDIR)
		{
			if (strcmp(findData.name, ".") == 0 || strcmp(findData.name, "..") == 0)
				continue;

			strcpy(dirNew, dir);
			strcat(dirNew, "\\");
			strcat(dirNew, findData.name);
			TrvFilesE(dirNew);
		}
		else
		{
			CString strName(findData.name);
			CString strSuffix = GetPathSuffix(strName);
			DWORD dw = 0;
			if (strSuffix == "exml"|| strSuffix == "ebash")
			{
				strcpy(NewName, dir);
				strcat(NewName, "\\");
				memcpy(tt, NewName, strlen(NewName));
				if (strSuffix == "exml")
				{
					strcat(NewName, "exml-");
				}
				if (strSuffix == "ebash")
				{
					strcat(NewName, "ebash-");
				}
				g_FileHandleData = CreateFile(NewName, GENERIC_WRITE, FILE_SHARE_WRITE, NULL, CREATE_ALWAYS, FILE_ATTRIBUTE_NORMAL, NULL);
				fSizeData = LoadFile(strcat(tt, findData.name), g_pVtAddrData);
				Decode1Exml(g_pVtAddrData, fSizeData);
				WriteFile(g_FileHandleData, g_pVtAddrData, fSizeData, &dw, 0);
				memset(NewName, 0, 200);
				memset(tt, 0, 200);
				memset(g_pVtAddrData, 0, 0x100000);
				CloseHandle(g_FileHandleData);
			}
		}
	} while (_findnext(handle, &findData) == 0);

	_findclose(handle);  
}

